import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, TouchableOpacity, View } from 'react-native';
import ProgressArc from './components/ProgressArc';
import { useState } from 'react';

const messages = [
  'nie tykaj',
  'zomstaw mnie',
  'zrob cos pozytecznego',
  'zacznij sie uczyc',
  'znajdz prace',
  'laskocze'
];

export default function App() {
  const [message, setMessage] = useState('');

  const [percentage, setPercentage] = useState(2);
  const handleRandomize = () => {
    const randomPercentage = Math.floor(Math.random() * 101); // Random number between 0 and 100
    setPercentage(randomPercentage);
  };

  const handlePress = () => {
    var randomDecimal = Math.random();
    var randomNumberInRange = Math.floor(randomDecimal * 5);
    setMessage(messages[randomNumberInRange]);
  };
  return (
    <View style={styles.container}>
      <View style={styles.arcContainer}>
        <ProgressArc percentage={percentage} />
      </View>
      {/* <TouchableOpacity
        style={styles.button}
        onPress={handleRandomize}
      >
        <Text>Press me</Text>
      </TouchableOpacity> */}
      {/* <Text>{message}</Text>
      <TouchableOpacity
        style={styles.head}
        onPress={handlePress}
      >
        <View style={styles.eyes}>
          <View style={styles.leftEye}></View>
          <View style={styles.rightEye}></View>
        </View>
        <View style={styles.mouth}></View>
      </TouchableOpacity> */}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
    // flexDirection: 'column',
    width: 100,
    height: 100
  },
  button: {
    flex: 1,
    position: 'absolute',
    backgroundColor: 'lightblue',
    alignItems: 'center',
    justifyContent: 'center',
    height: 100,
    width: 100
  },
  arcContainer: {
    borderStyle: 'solid',
    borderWidth: 10,
    borderRadius: 5,
    borderColor: 'black',
    height: 100,
    width: 100
  }
});
