import { StatusBar } from 'expo-status-bar';
import {
  SafeAreaView,
  StyleSheet,
  Text,
  TouchableOpacity,
  View
} from 'react-native';
import ProgressArc from './components/ProgressArc';
import { useState } from 'react';

export default function App() {
  const [percentage, setPercentage] = useState(2);

  return (
    <SafeAreaView style={styles.container}>
      <View
        style={styles.arcContainer}
        id="arcContainer"
      >
        <ProgressArc
          percentage={percentage}
          setPercentage={setPercentage}
        />
        <View style={styles.caloriesColumn}>
          <Text style={styles.topText}>630 kcal</Text>
          <Text style={styles.botText}>left</Text>
          <Text style={styles.topText}>1040 kcal</Text>
          <Text style={styles.botText}>consumed</Text>
          <Text style={styles.topText}>1600 kcal</Text>
          <Text style={styles.botText}>total</Text>
        </View>
      </View>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#ffffff',
    alignContent: 'center',
    justifyContent: 'center',
    padding: 70
  },
  arcContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between'
  },
  caloriesColumn: {
    justifyContent: 'space-between'
  },
  topText: {
    fontWeight: 'bold',
    padding: 0
  },
  botText: {
    opacity: 0.5,
    paddingBottom: 10
  }
});
