import React, { useRef, useEffect } from 'react';
import { Text, Dimensions, Animated } from 'react-native';
import { Svg, Path } from 'react-native-svg';

export const ProgressArc = ({ percentage }) => {
  const circumference = r * A;
  const translation = useRef(new Animated.Value(0)).current;
  const { width } = Dimensions.get('window');
  const size = width - 32;
  const strokeWidth = 50;
  const AnimatedPath = Animated.createAnimatedComponent(Path);
  const { PI, cos, sin } = Math;
  const r = (size - strokeWidth) / 2;
  const cx = size / 2;
  const cy = size / 2;
  const A = PI + PI * 0.4;
  const startAngle = PI + PI * 0.2;
  const endAngle = 2 * PI - PI * 0.2;
  // A rx ry x-axis-rotation large-arc-flag sweep-flag x y
  const x1 = cx - r * cos(startAngle);
  const y1 = -r * sin(startAngle) + cy;
  const x2 = cx - r * cos(endAngle);
  const y2 = -r * sin(endAngle) + cy;
  const d = `M ${x2} ${y2} A ${r} ${r} 0 1 1 ${x1} ${y1}`;

  const strokeDashoffset = translation;

  useEffect(() => {
    Animated.timing(translation, {
      toValue: (724.5 * percentage) / 100,
      duration: 1000,
      useNativeDriver: true
    }).start();
  }, [percentage]);
  return (
    <Svg
      width={size}
      height={size}
    >
      <Text>{100 - percentage}%</Text>
      <Path
        d={d}
        stroke="#cccccc"
        strokeWidth={strokeWidth}
        strokeLinecap="round"
        strokeLinejoin="round"
        fill={'none'}
      />
      <AnimatedPath
        stroke="#5460c4"
        fill="none"
        strokeLinecap="round"
        strokeLinejoin="round"
        strokeDasharray={`${circumference}, ${circumference}`}
        {...{ d, strokeDashoffset, strokeWidth }}
      />
    </Svg>
  );
};

export default ProgressArc;
