import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, TouchableOpacity, View } from 'react-native';
import ProgressArc from './components/ProgressArc';
import { useState } from 'react';

const messages = [
  'nie tykaj',
  'zomstaw mnie',
  'zrob cos pozytecznego',
  'zacznij sie uczyc',
  'znajdz prace',
  'laskocze'
];

export default function App() {
  const [width, setWidth] = useState(0);

  const [percentage, setPercentage] = useState(2);
  const handleRandomize = () => {
    const randomPercentage = Math.floor(Math.random() * 101); // Random number between 0 and 100
    setPercentage(randomPercentage);
  };

  const handlePress = () => {
    var randomDecimal = Math.random();
    var randomNumberInRange = Math.floor(randomDecimal * 5);
    setMessage(messages[randomNumberInRange]);
  };
  return (
    <View style={styles.container}>
      {/* <View style={styles.arcContainer}> */}
      {/* <View
        style={styles.arcContainer}
        onLayout={(event) => {
          var { x, y, width, height } = event.nativeEvent.layout;
          setWidth(width);
        }}
      > */}
      <ProgressArc
        percentage={percentage}
        width={width}
      />
      {/* </View> */}
      <TouchableOpacity
        style={styles.button}
        onPress={handleRandomize}
      >
        <View>
          <Text style={styles.button}>{100 - percentage}%</Text>
          <Text style={{ opacity: 0.5 }}>of daily goal</Text>
        </View>
      </TouchableOpacity>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center'
    // flexDirection: 'column'
  },
  button: {
    flex: 1,
    position: 'absolute',
    // backgroundColor: 'lightblue',
    alignItems: 'center',
    justifyContent: 'center',
    fontSize: 30
    // height: 100,
    // width: 100
  },
  arcContainer: {
    borderStyle: 'solid',
    borderWidth: 10,
    borderRadius: 5,
    borderColor: 'black',
    height: 300,
    width: 300
  }
});
