import React, { useRef, useEffect } from 'react';
import {
  Text,
  Dimensions,
  Animated,
  View,
  TouchableOpacity,
  StyleSheet
} from 'react-native';
import { Svg, Path } from 'react-native-svg';

var { width } = Dimensions.get('window');
width = width / 2;
const size = width - 32;
const strokeWidth = 25;
const AnimatedPath = Animated.createAnimatedComponent(Path);
const { PI, cos, sin } = Math;
const r = (size - strokeWidth) / 2;
const cx = size / 2;
const cy = size / 2;
const A = PI + PI * 0.4;
const startAngle = PI + PI * 0.2;
const endAngle = 2 * PI - PI * 0.2;
// A rx ry x-axis-rotation large-arc-flag sweep-flag x y
const x1 = cx - r * cos(startAngle);
const y1 = -r * sin(startAngle) + cy;
const x2 = cx - r * cos(endAngle);
const y2 = -r * sin(endAngle) + cy;
const d = `M ${x2} ${y2} A ${r} ${r} 0 1 1 ${x1} ${y1}`;

export const ProgressArc = ({ percentage, setPercentage }) => {
  const circumference = r * A;
  const translation = useRef(new Animated.Value(0)).current;

  const strokeDashoffset = translation;

  const handleRandomize = () => {
    const randomPercentage = Math.floor(Math.random() * 101); // Random number between 0 and 100
    setPercentage(randomPercentage);
  };

  useEffect(() => {
    Animated.timing(translation, {
      toValue: (circumference * percentage) / 100,
      duration: 1000,
      useNativeDriver: true
    }).start();
  }, [percentage]);
  return (
    <View style={styles.container}>
      <Svg
        width={size}
        height={size}
      >
        <Path
          d={d}
          stroke="#cccccc"
          strokeWidth={strokeWidth}
          strokeLinecap="round"
          strokeLinejoin="round"
          fill={'none'}
        />
        <AnimatedPath
          stroke="#5460c4"
          fill="none"
          strokeLinecap="round"
          strokeLinejoin="round"
          strokeDasharray={`${circumference}, ${circumference}`}
          {...{ d, strokeDashoffset, strokeWidth }}
        />
      </Svg>
      <TouchableOpacity
        style={styles.button}
        onPress={handleRandomize}
      >
        <Text style={{ color: 'white', opacity: 0.5 }}>Press me </Text>
        <Text style={{ color: 'white', fontSize: 30 }}>
          {100 - percentage}%
        </Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignContent: 'center',
    justifyContent: 'center',
    flexDirection: 'row'
  },
  button: {
    // flex: 1,
    position: 'absolute',
    paddingTop: 20
    // alignContent: 'center',
    // justifyContent: 'center'
  }
});

export default ProgressArc;
