import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, TouchableOpacity, View } from 'react-native';
import ProgressArc from './components/ProgressArc';
import { useState } from 'react';

const messages = [
  'nie tykaj',
  'zomstaw mnie',
  'zrob cos pozytecznego',
  'zacznij sie uczyc',
  'znajdz prace',
  'laskocze',
  'wypierdalaj',
  'uwu kawai',
  'to nazywasz dotykiem?',
  'twoja matka mocniej bije'
];

export default function App() {
  const [message, setMessage] = useState('');

  const [percentage, setPercentage] = useState(2);
  const handleRandomize = () => {
    const randomPercentage = Math.floor(Math.random() * 101); // Random number between 0 and 100
    setPercentage(randomPercentage);
  };

  const handlePress = () => {
    var randomDecimal = Math.random();
    var randomNumberInRange = Math.floor(randomDecimal * messages.length);
    setMessage(messages[randomNumberInRange]);
  };
  return (
    <View style={styles.container}>
      {/* <ProgressArc percentage={percentage} />
      <TouchableOpacity
        style={styles.button}
        onPress={handleRandomize}
      >
        <Text>Press me</Text>
      </TouchableOpacity> */}
      <Text>{message}</Text>
      <TouchableOpacity
        style={styles.head}
        onPress={handlePress}
      >
        <View style={styles.eyes}>
          <View style={styles.leftEye}></View>
          <View style={styles.rightEye}></View>
        </View>
        <View style={styles.mouth}></View>
      </TouchableOpacity>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center'
    // flexDirection: 'column'
  },
  button: {
    flex: 1,
    position: 'absolute',
    backgroundColor: 'lightblue',
    alignItems: 'center',
    justifyContent: 'center',
    height: 100,
    width: 100
  },
  head: {
    // flex: 1,
    borderStyle: 'solid',
    borderWidth: 5,
    borderColor: 'black',
    borderRadius: 10,
    width: 80,
    height: 80,
    backgroundColor: 'lightpink',
    justifyContent: 'center'
  },
  eyes: {
    flexDirection: 'row',
    adjustItems: 'space-between',
    justifyContent: 'space-between',
    padding: 10
  },
  leftEye: {
    borderStyle: 'solid',
    borderWidth: 5,
    borderColor: 'black',
    borderRadius: 10,
    width: 20,
    height: 20,
    backgroundColor: 'blue'
  },
  rightEye: {
    borderStyle: 'solid',
    borderWidth: 5,
    borderColor: 'black',
    borderRadius: 10,
    width: 20,
    height: 20,
    backgroundColor: 'blue'
  },
  mouth: {
    width: 40,
    height: 10,
    backgroundColor: 'black',
    borderStyle: 'solid',
    borderWidth: 5,
    borderColor: 'black',
    borderRadius: 10,
    alignSelf: 'center'
  }
});
