import { StatusBar } from 'expo-status-bar';
import {
  SafeAreaView,
  StyleSheet,
  Text,
  TouchableOpacity,
  View
} from 'react-native';
import ProgressArc from './components/ProgressArc';
import { useState } from 'react';

export default function App() {
  const [width, setWidth] = useState(0);
  const [percentage, setPercentage] = useState(2);

  const handleRandomize = () => {
    const randomPercentage = Math.floor(Math.random() * 101); // Random number between 0 and 100
    setPercentage(randomPercentage);
  };
  return (
    <SafeAreaView style={styles.container}>
      {/* <View style={styles.container}> */}
      <View style={[styles.arcContainer, styles.shadowProp]}>
        <ProgressArc
          percentage={percentage}
          width={width}
        />
        <TouchableOpacity
          style={styles.button}
          onPress={handleRandomize}
        >
          <Text style={{ opacity: 0.5 }}>Press me </Text>
          <Text style={{ fontSize: 30 }}>{100 - percentage}%</Text>
        </TouchableOpacity>
      </View>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center'
  },
  button: {
    flex: 1,
    position: 'absolute',
    alignItems: 'center',
    justifyContent: 'center'
  },
  arcContainer: {
    // flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    height: 200,
    width: 200
  },
  shadowProp: {
    shadowColor: '#171717',
    shadowOffset: { width: -2, height: 4 },
    shadowOpacity: 0.2,
    shadowRadius: 20,
    elevation: 3
  }
});
